<script>
import gsap from 'gsap'
import AppButton from '~/components/AppButton.vue'

export default {
  name: 'DefaultPage',
  components: { AppButton },
  data() {
    return {
      menu: false,
    }
  },
  head() {
    return {
      script: [
        {
          json: {
            '@context': 'https://schema.org',
            '@type': 'Corporation',
            name: 'Orunla Africa',
            alternateName: 'Orunla',
            email: 'hi@orunla.ng',
            url: 'https://orunla.ng',
            logo: 'https://res.cloudinary.com/orunla/image/upload/v1679113100/Orunla/fqypmi0ecroup7miero4.jpg',
            sameAs: [
              'https://orunla.ng',
              'https://linkedin.com/company/orunla-africa',
              'https://instagram.com/orunla_africa',
              'https://x.com/orunla_africa',
            ],
          },
          type: 'application/ld+json',
        },
        // {
        //   json: {
        //     '@context': 'https://schema.org',
        //     '@type': 'Website',
        //     name: 'Orunla',
        //     alternateName: ['Orunla Africa', 'Orunla Book store Africa'],
        //     url: 'https://orunla.ng',
        //   },
        //   type: 'application/ld+json',
        // },
      ],
    }
  },
  watch: {
    $route: function () {
      if (this.menu) {
        this.toggleMenu()
      }
    },
  },
  methods: {
    toggleMenu() {
      const hamburger = this.$refs.hamburger
      if (!this.menu) {
        gsap.to(
          'ul',
          {
            translateX: 0,
            autoAlpha: 1,
            ease: 'circ.in',
          },
          0.5
        )
        hamburger.classList.add('hamburger--active')
        // document.body.scrollIntoView({ behavior: 'smooth'})
      } else {
        gsap.to(
          'ul',
          {
            autoAlpha: 0,
            translateX: '-100%',
            ease: 'circ.in',
          },
          0.6
        )
        hamburger.classList.remove('hamburger--active')
      }

      this.menu = !this.menu
    },
  },
}
</script>

<style>
.hamburger {
  display: block;
  cursor: pointer;
  padding: 1rem;
}

.hamburger__line {
  margin: 3px 0 0;
  padding: 1.5px 15px;
  background-color: #342b8e;
  transition: all linear 0.3s;
}

.hamburger--active .hamburger__line:first-of-type {
  transform: rotate(45deg) translate(1px, 1px);
}

.hamburger--active .hamburger__line:nth-of-type(even) {
  display: none;
}

.hamburger--active .hamburger__line:last-of-type {
  transform: rotate(-45deg) translate(3px, -4px);
}
</style>
